mapper.update:
update t_banner set
 imageUrl=#{imageUrl},
 targetUrl=#{targetUrl},
 sortNo=#{sortNo}
 where id=#{id}

mapper.select:
select * from t_bdinfo
name=#{name}
order by id desc

select * from t_article
kind=#${cond.kind}
order by id desc

mapper.update:
update t_article set
 title=#{title},
 content=#{content},
 kind=#{kind},
 path=#{path}
 where id=#{id}
