mapper.select:
select * 
from t_project
where taskId=#{taskId} and userId=#{userId}

select count(*) 
from t_project


    and taskId=#{cond.taskId}


    and userId=#{cond.userId}


    and state=#{cond.state}


    and selected=#{cond.selected}

select p.*, u.nickname designerName, u.mobile designerMobile, u.avatar designerAvatar, u.special designerSpecial from t_project p inner join t_user u on u.id=p.userId
and p.taskId=#{cond.taskId}
and p.userId=#{cond.userId}
and p.state=#{cond.state}
and p.selected=#{cond.selected}
order by
selected desc, id desc
id desc

SELECT count(*)
FROM t_project
where taskId=#{taskId}
