mapper.select:
			select count(*) 
      from t_taskinfo


          userId=#{cond.userId}


          and cateId=#{cond.cate1}


          and taskMode=#{cond.mode}


          and service=#{cond.service}


          and instr(requirement, #{cond.keyword}) > 0


          and instr(username, #{cond.username}) > 0


          and taskState=#{cond.taskState}


          and taskState <> 6


          and auditState=#{cond.auditState}


          and invoice=#{cond.invoice}

select t.*, 
  u.nickname username
from t_taskinfo t
  inner join t_user u on u.id=t.userId


    t.userId=#{cond.userId}


    and t.cateId=#{cond.cate1}


    and t.taskMode=#{cond.mode}


    and t.service=#{cond.service}


    and instr(t.requirement, #{cond.keyword}) > 0


    and instr(u.username, #{cond.username}) > 0


    and t.taskState=#{cond.taskState}


    and t.taskState <> 6


    and t.auditState=#{cond.auditState}


    and t.invoice=#{cond.invoice}



  addTime desc,
  addTime asc,
  dueTime desc,
  dueTime asc,
  totalAmount desc,
  totalAmount asc,

mapper.delete:
delete 
from t_taskinfo
where id=#{id} and userId=#{userId}
