mapper.select:
SELECT ifnull(sum(likeCount), 0) totalCount
FROM t_zuopininfo
WHERE userId=#{userId}

			SELECT count(*) 
      FROM t_zuopininfo


          AND userId=#{cond.userId}


          AND province = #{cond.province}


          AND (cateId/1000)*1000=${cond.cate1}


          AND cateId=#{cond.cate2}


          AND userId IN (select id from t_users where type=1 and realAuthState=1)


          AND userId IN (select id from t_users where type=1 and abilityGrade > 0)


          AND userId IN (select id from t_users where type=2 and realAuthState=1)


          AND userId IN (select id from t_users where type=2 and abilityGrade > 0)


          AND INSTR(title, #{cond.keyword#}) > 0

mapper.update:
update t_zuopininfo set
  topNo=#{topNo}
  where id=#{id}

mapper.delete:
delete 
from t_zuopininfo
where id=#Id# and userId=#{userId}
